import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "../NavBar.css";

const NavBar = () => {
  const location = useLocation();
  const [theme, setTheme] = useState("dark-theme"); // Initialiseer thema met 'dark'

  const toggleTheme = () =>
    setTheme(theme === "dark-theme" ? "light-theme" : "dark-theme");

  return (
    <nav className={`navbar navbar-expand-lg ${theme}`}>
      <div className="container-fluid">
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <Link
                className={`nav-link ${
                  location.pathname === "/" ? "active" : ""
                }`}
                to="/"
              >
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link
                className={`nav-link ${
                  location.pathname === "/voertuigen" ? "active" : ""
                }`}
                to="/voertuigen"
              >
                Bekijk Voertuigen
              </Link>
            </li>
            <li className="nav-item">
              <Link
                className={`nav-link ${
                  location.pathname === "/chauffeurs" ? "active" : ""
                }`}
                to="/chauffeurs"
              >
                Bekijk Chauffeurs
              </Link>
            </li>
            <li className="nav-item">
              <Link
                className={`nav-link ${
                  location.pathname === "/reserveringen" ? "active" : ""
                }`}
                to="/reserveringen"
              >
                Bekijk Reserveringen
              </Link>
            </li>
          </ul>
          <button
            className="btn btn-outline-secondary ms-auto"
            onClick={toggleTheme}
          >
            {theme === "dark-theme" ? "Switch to Light Theme" : "Switch to Dark Theme"}
          </button>
        </div>
      </div>
    </nav>
  );
};

export default NavBar;
